/**
 * @param {object} plugin - The current discovery plugin that is running.
 * @param {object} contentSchema - The current content record.
 * @param {object} data - The data is variable and is dependent upon the selected event.
 * @param {object} configurations - The available API keys.
 */
async function runScript(plugin, contentSchema, data, configurations) {
    // PUT CODE BELOW THIS LINE
    log.info(`running ${plugin.label}`);
    const latitude = doc.documentNode.selectSingleNode("//*[@id='map']").getAttributeValue('data-latitude', null);
    const longitude = doc.documentNode.selectSingleNode("//*[@id='map']").getAttributeValue('data-longitude', null);
    const startedOn = doc.documentNode.SelectNodes("//time[contains(@class, 'timeago')]").firstOrDefault().getAttributeValue('datetime', null);

    contentSchema.contentLatitude = latitude;
    contentSchema.contentLongitude = longitude;
    contentSchema.contentStartedOn = startedOn;    

    const imagesLookup = contentSchema.extractImages();
    const collection = doc.documentNode.selectNodes("//img[contains(@alt,'thumbnail')]");
    const nodes = contentSchema.contentNodes;

    for (const node of collection) {
        let src = node.getAttributeValue('src', '');
        log.info(`src is ${src}`);
        if (src.endsWith('_50x50c.jpg')) {
            src = src.replace('_50x50c.jpg', '_600x450.jpg');
        } 
        const base64image = imagesLookup[src];
        let newContentSchema = contentSchema.clone(base64image);
        newContentSchema.contentScreenShot = base64image;
        newContentSchema.contentUrl = src;        
        nodes.add(newContentSchema);
    }

    contentSchema.contentNodes = nodes;
    // PUT CODE ABOVE THIS LINE
}
